images/asus-tinker-board/2.19.0+rev5.pro.Body:
{
	"slug": "asus-tinker-board",
	"version": 1,
	"aliases": [
		"asus-tinker-board"
	],
	"name": "Asus Tinker Board (EXPERIMENTAL)",
	"arch": "armv7hf",
	"state": "EXPERIMENTAL",
	"instructions": [
		"Write the OS file you downloaded to your SD card. We recommend using <a href=\"http://www.etcher.io/\">Etcher</a>.",
		"Insert the freshly burnt SD card into the Asus Tinker Board.",
		"Connect your Asus Tinker Board to the internet, then power it up."
	],
	"gettingStartedLink": {
		"windows": "https://docs.resin.io/asus-tinker-board/nodejs/getting-started/#adding-your-first-device",
		"osx": "https://docs.resin.io/asus-tinker-board/nodejs/getting-started/#adding-your-first-device",
		"linux": "https://docs.resin.io/asus-tinker-board/nodejs/getting-started/#adding-your-first-device"
	},
	"supportsBlink": true,
	"yocto": {
		"machine": "asus-tinker-board",
		"image": "resin-image",
		"fstype": "resinos-img",
		"version": "yocto-rocko",
		"deployArtifact": "resin-image-asus-tinker-board.resinos-img",
		"compressed": true
	},
	"options": [
		{
			"isGroup": true,
			"name": "network",
			"message": "Network",
			"options": [
				{
					"message": "Network Connection",
					"name": "network",
					"type": "list",
					"choices": [
						"ethernet",
						"wifi"
					]
				},
				{
					"message": "Wifi SSID",
					"name": "wifiSsid",
					"type": "text",
					"when": {
						"network": "wifi"
					}
				},
				{
					"message": "Wifi Passphrase",
					"name": "wifiKey",
					"type": "password",
					"when": {
						"network": "wifi"
					}
				}
			]
		},
		{
			"isGroup": true,
			"isCollapsible": true,
			"collapsed": true,
			"name": "advanced",
			"message": "Advanced",
			"options": [
				{
					"message": "Check for updates every X minutes",
					"name": "appUpdatePollInterval",
					"type": "number",
					"min": 10,
					"default": 10
				}
			]
		}
	],
	"configuration": {
		"config": {
			"partition": {
				"primary": 1
			},
			"path": "/config.json"
		}
	},
	"initialization": {
		"options": [
			{
				"message": "Select a drive",
				"type": "drive",
				"name": "drive"
			}
		],
		"operations": [
			{
				"command": "burn"
			}
		]
	}
}

